import { React, useEffect, useState }  from "react"
import { useParams } from "react-router-dom"
import styled from "styled-components"
import { Link } from "react-router-dom"

const ActionDetails = () => {
    const [showImg, setImg] = useState(null)
    const [showName, setName] = useState(null)

    var { action } = useParams()

    useEffect(() => {   
        const getData = async () => {
            await fetch('http://localhost:3002/post-action', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({action: action})
            })
            const response = await fetch('http://localhost:3002/action-details')
            const output = await response.json()

            setName(output.subHeader.name)
            setImg(output.subHeader.src)
        }
        getData()
    }, [])

    return(
        <>
            <SubHeader>
                <div style={{padding: '20px 0px 5px 0px', borderBottom: 'solid #ccc 1px'}}>
                    <Link to='/' style={{textDecoration: 'none'}}>
                        <Home>Home </Home>
                    </Link>

                    <span style={{color:'rgb(165, 170, 177)' }}>/ </span>

                    <Link to='/acoes/page/1' style={{textDecoration: 'none'}}>
                        <Home>Ações</Home>
                    </Link>

                    <span style={{color:'rgb(165, 170, 177)'}}> / </span>
                    <span style={{color:'rgb(165, 170, 177)'}}>{action}</span>
                </div>  

                <div style={{display: 'flex', alignItems: 'center'}}>
                    <DivImg>
                        <img style={{height: '80px', width: '80px', borderRadius: '10px'}} alt='' src={showImg}></img>
                    </DivImg>   

                    <div>
                        <h2 style={{margin: '0'}}>{action}</h2>
                        <p style={{margin: '0'}}>{showName}</p>
                    </div>
                </div>
            </SubHeader>
        </>
    )
}

const SubHeader = styled.div`
    background-color: #173e5b;
    padding: 0px 18% 0px 18%;
`

const DivImg = styled.div`
    border-radius: 10px;
    height: 100px;
    width: 100px;
    padding: 10px;
    background-color: white;
`

const Home = styled.span`
    color:rgb(165, 170, 177);
    cursor: pointer;

    &:hover{
        transition: 0.2s ease-in-out;
        color: white;
    }
`

export default ActionDetails
