import { React, useEffect } from "react"
import styled from "styled-components"
import { useParams } from "react-router-dom"
 
const Pagination = () => {
    useEffect(() => {
        var { pageUrl } = useParams()

        document.getElementById('next').addEventListener('click', () => {
            if(pageUrl === '9') window.location.href = `${pageUrl}`
            
            else{
                pageUrl = pageUrl++
                window.location.href = `${pageUrl}`
            } 
        })
        
        document.getElementById('before').addEventListener('click', () => {
            if(pageUrl === '1') window.location.href = `${pageUrl}`
            
            else{
                pageUrl = pageUrl--
                window.location.href = `${pageUrl}`
            }  
        })
    }, )

    const handleClick = (event) => {
        const page = event.target.innerHTML
        window.location.href = `${page}`
    }

    return(
        <Div>
            <PBtn id="next" onClick={handleClickBtn}>{'<'} Anterior</PBtn>

            <div style={{display: 'flex', justifyContent: 'space-between'}}>
                <PNumber onClick={handleClick}>1</PNumber>
                <PNumber onClick={handleClick}>2</PNumber>
                <PNumber onClick={handleClick}>3</PNumber>
                <PNumber onClick={handleClick}>4</PNumber>
                <PNumber onClick={handleClick}>5</PNumber>
                <PNumber onClick={handleClick}>6</PNumber>
                <PNumber onClick={handleClick}>7</PNumber>
                <PNumber onClick={handleClick}>8</PNumber>
                <PNumber onClick={handleClick} style={{margin: '0px'}}>9</PNumber>
            </div>

            <PBtn id="before" onClick={handleClickBtn}>PrÃ³xima {'>'}</PBtn>
        </Div>
    )
}

const Div = styled.div`
    display: flex;
    justify-content: space-evenly;
    width: 100%;
    margin-top: 40px;
`

const PBtn = styled.p`
    padding: 10px 20px 10px 20px;
    background-color: #32384d;
    cursor: pointer;
    border-radius: 10px;
    color: white;
    display: flex;
    justify-content: center;
    align-items: center;
`

const PNumber = styled.p`
    padding: 10px;
    background-color: #32384d;
    cursor: pointer;
    border-radius: 10px;
    color: white;
    margin-right: 10px;
    height: 40px;
    width: 40px;
    display: flex;
    justify-content: center;
    align-items: center;
`

export default Pagination
