import React, { useEffect, useState } from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";
import { useMediaQuery } from "react-responsive";

const Home = () => {
    const for1330Width = useMediaQuery({query: '(max-width: 1330px)'})
    const for1200Width = useMediaQuery({query: '(max-width: 1200px)'})

    const [showData, setData] = useState([])
    const [showSearch, setSearch] = useState({})
    const [flag, setFlag] = useState(false)

    useEffect(() => {
        const getData = async() => {
            const response = await fetch('http://localhost:3000/data-home')
            const data = await response.json()

            setData(data)
        }
        getData()
    }, [])

    const handleChange = () => {
        const text = document.getElementById('input').value

        const names = []
        const actions = []
        const imgs = []
        const cotacao = []

        if(text.length >= 3){
            showData.data.names.forEach((element, index) => {
                if(element.toLowerCase().includes(text.toLowerCase()) || showData.data.actions[index].toLowerCase().includes(text.toLowerCase())){
                    names.push(element)
                    actions.push(showData.data.actions[index])
                    imgs.push(showData.data.img[index])
                    cotacao.push(showData.data.cotacao[index])
                }
                
                if(index == showData.data.names.length - 1 && names.length > 0){
                    setSearch({
                        names,
                        actions,
                        imgs,
                        cotacao
                    })
                    setFlag(true)
                }
            })
        } 
        else setFlag(false)
    }

    useEffect(() => {
        document.getElementById('root').addEventListener('click', event => {
            if(event.target !== document.getElementById('absolute')){
                setFlag(false)
            }
        })
    }, [])

    return(
        <>  
            {!for1200Width &&
                <></>
            }
        </>
    )
}

const HeaderStyle = styled.header`
    background-color: #173e5b;
    padding: 110px 16% 70px 16%;
`

const H1 = styled.h1`
    inline-size: 570px;
    overflow-wrap: break-word;
    line-height: 50px;
    font-weight: 700;
    color: white;
    margin: 0; 
    font-size: 50px;
`

const P = styled.p`
    color: white;
    inline-size: 600px;
    overflow-wrap: break-word;
    font-weight: 300;
    margin-top: 10px;
    margin-bottom: 20px;
`

const A = styled.a`
    text-decoration: none;
    color: white;
    padding: 10px 20px 10px 20px;
    background-color: hsl(213 100% 51%);
    border-radius: 5px;
    cursor: pointer;

    &:hover{
        transition: 0.2s ease-in-out;
        color: black;
    }
`

const Container = styled.div`
    background-color: #101318; 
    padding: 70px 16% 80px 16%;
    display: flex;
    flex-direction: column;
    align-items: center;
`

const Input = styled.input`
    color: #ccc;
    width: 800px;
    border-radius: 20px;
    padding: 8px 13px 8px 13px;
    background-color: #101318;
    border: solid 1px hsl(206 61% 65%);

    &:focus{
        outline: #173e5b;
    }
`

const Actions = styled.div`
    background-color: #171b26;
    border-radius: 10px;
    padding: 40px 30px 40px 30px;
    margin-top: 80px;
`

const DivCard = styled.div`
  border-radius: 10px;
  background-color: #32384d;
  margin: 10px 10px 10px 10px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  text-align: center;
  width: 200px;
  height: 220px;
  cursor: pointer;
  padding: 0px 15px 0px 15px;
`

const DivWrap = styled.div`
    flex-wrap: wrap; 
    display: flex;
    justify-content: center;
    gap: 10px;
    margin-top: 30px;
    color: white;
`

const Img = styled.img`
    height: 56px;
    width: 56px;
    border-radius: 5px;
    margin-bottom: 5px;
`

const PCard = styled.p`
    font-weight: 600;
    margin-bottom: 5px;
`

const Absolute = styled.div`
    position: absolute;
    background-color: #171b26;
    display: flex;
    flex-direction: column;
    align-items: center;
    color: white;
    width: 100%;
    margin-top: 20px;
    border: solid 1px hsl(206 61% 65%);
    border-radius: 10px;
`

const DivResult = styled.div`
    display: flex;
    align-items: center; 
    justify-content: space-between;
    width: 100%;
    padding: 15px 20px 15px 20px;
    border-bottom: solid 1px #ccc;

    > *{
      &:last-child {
         border: solid 0px #ccc;
      }
    }
`

export default Home
