import { useState, useEffect } from 'react'
import styled from 'styled-components'

function ShowCards() {
  const [imgs, setImgs] = useState([])
  const [indicators, setIndicators] = useState([])
  const [title, setTitle] = useState([])

  useEffect(() => {
    const getData = async () => {
      const response = await fetch("http://localhost:3000/data-scrapper")
      const output = await response.json()

      setImgs(output.imgs)
      setIndicators(output.indicators)
      setTitle(output.title)
    }
    getData()
  }, [])

  return (
    <div style={{backgroundColor: '#101318', padding: '80px 18% 0px 18%'}}>
      <div style={{Flex}}>
        {imgs.map((img, index) => {
            return(
              <DivCard key={index}>
                <img src={img} alt=''></img>

                <h3>{title[index]}</h3>

                <p>P/L {indicators[index].pl}</p>
                <p>P/VP {indicators[index].pvp}</p>
                <p>DY {indicators[index].dy}</p>
                <p>ROE {indicators[index].roe}</p>
              </DivCard>
            ) 
          })
        }
      </div>
    </div>
  );
}

const DivCard = styled.div`
  border-radius: 10px;
  background-color: #32384d;
  height: 230px;
  width: 180px;
`

export default ShowCards
