import React, { useEffect, useState } from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";

const Home = () => {
    const [showData, setData] = useState([])

    useEffect(() => {
        const getData = async() => {
            const response = await fetch('http://localhost:3000/data-home')
            const data = await response.json()
            
            setData(data)
        }
        getData()
    }, [])

    return(
        <>
            <HeaderStyle>
                <div>
                    <H1>Decida no que investir em segundos</H1>
                    <P>Nossa ferramenta oferece acesso a dados atualizados, ajudando você a tomar decisões fundamentadas com confiança.</P>
                    <Link style={{textDecoration: 'none'}} to={'acoes/page/1'}>
                        <A>Ver ativos</A>
                    </Link>
                </div>
            </HeaderStyle>
        </>
    )
}

const HeaderStyle = styled.header`
    background-color: #173e5b;
    padding: 110px 16% 70px 16%;
`

const H1 = styled.h1`
    inline-size: 570px;
    overflow-wrap: break-word;
    line-height: 50px;
    font-weight: 700;
    color: white;
    margin: 0; 
    font-size: 50px;
`

const P = styled.p`
    color: white;
    inline-size: 600px;
    overflow-wrap: break-word;
    font-weight: 300;
    margin-top: 10px;
`

const A = styled.a`
    text-decoration: none;
    color: white;
    padding: 10px 15px 10px 15px;
    background-color: hsl(213 100% 51%);
    border-radius: 5px;
    cursor: pointer;
    transition: ease-in-out 0.5s

    &:hover{
        transition: 0.2s ease-in-out;
        color: black;
    }
`

export default Home
