import { useState, useEffect } from 'react'
import styled from 'styled-components'
import { useParams } from 'react-router-dom'

function ShowCards() {
  const [imgs, setImgs] = useState([])
  const [indicators, setIndicators] = useState([])
  const [title, setTitle] = useState([])

  useEffect(() => {
    const getData = async () => {
      const response = await fetch("http://localhost:3000/data-scrapper")
      const output = await response.json()

      setImgs(output.imgs)
      setIndicators(output.indicators)
      setTitle(output.title)
    }
    getData()
  }, [])

  return (
    <>
      {imgs.map((img, index) => {
            return(
              <DivCard key={index}>
                <DivImg>
                  <Img src={img} alt=''></Img>
                </DivImg>
              
                <div style={{padding: '20px 20px 0px 20px', display: 'flex', flexDirection: 'column', alignItems: 'center'}}>
                  <H3>{title[index]}</H3>

                  <div style={{display: 'flex'}}>
                    <div style={{marginRight: '20px'}}>
                      <P style={{marginBottom: '5px'}}>P/L: {indicators[index].pl}</P>
                      <P>DY: {indicators[index].dy}</P>
                    </div>

                    <div>
                      <P style={{marginBottom: '5px'}}>P/VP: {indicators[index].pvp}</P>
                      <P>ROE: {indicators[index].roe}</P>
                    </div>
                  </div>
                </div>
              </DivCard>
            ) 
          })
        }
    </>   
  )
}

const DivCard = styled.div`
  border-radius: 10px;
  background-color: #32384d;
  margin: 10px 10px 10px 10px;
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 250px;
  height: 300px;
  cursor: pointer;
`

const DivImg = styled.div`
  background-color: white;
  border-top-left-radius: 10px;
  border-top-right-radius: 10px;
  width: 100%;
  display: flex;
  justify-content: center;
  height: 150px;
`

const Img = styled.img`
  padding: 20px;
  width: 100%;
`

const H3 = styled.h3`
  text-align: center;
  font-size: 18px;
  color: #6faddc;
  font-weight: 500;
  margin-bottom: 15px;
`

const P = styled.p`
  color: white;
  font-size: 15px;
`

export default ShowCards
