import React, { useEffect, useState } from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";

const Home = () => {
    const [showData, setData] = useState([])
    const [showSearch, setSearch] = useState({})
    const [flag, setFlag] = useState(false)

    useEffect(() => {
        const getData = async() => {
            const response = await fetch('http://localhost:3000/data-home')
            const data = await response.json()
            
            console.log(data)
            setData(data)
        }
        getData()
    }, [])

    const handleChange = () => {
        const text = document.getElementById('input').value

        const names = []
        const actions = []
        const imgs = []
        const cotacao = []

        if(text.length >= 3){
            showData.data.names.forEach((element, index) => {
                if(element.toLowerCase().includes(text.toLowerCase()) || showData.data.actions[index].toLowerCase().includes(text.toLowerCase())){
                    names.push(element)
                    actions.push(showData.data.actions[index])
                    imgs.push(showData.data.img[index])
                    cotacao.push(showData.data.cotacao[index])

                    if(index === showData.data.names.len)
                    setSearch({
                        names,
                        actions,
                        imgs,
                        cotacao
                    })
                }
            })
            setFlag(true)
            console.log(showSearch)
        }
    }

    return(
        <>
            <HeaderStyle>
                <div>
                    <H1>Decida no que investir em segundos</H1>
                    <P>Nossa ferramenta oferece acesso a dados atualizados, ajudando você a tomar decisões fundamentadas com confiança.</P>
                    <Link style={{textDecoration: 'none'}} to={'acoes/page/1'}>
                        <A>Ver ativos</A>
                    </Link>
                </div>
            </HeaderStyle>

            <Container>
                <div style={{position: 'relative'}}>
                    <Input id="input" onChange={handleChange} placeholder="Pesquise pelo ativo desejado" type="text"></Input>

                    {flag &&
                        <Absolute>
                            {showSearch.names.map((index, element) => {
                                return(
                                    <div key={index}>
                                        <img src={showSearch.imgs[index]}></img>
                                        <p>{showSearch.actions[index]}</p>
                                    </div>
                                )
                            })
                            }
                        </Absolute>
                    }
                </div>

                <Actions>
                    <h5 style={{color: 'white', margin: '0px', fontSize: '18px'}}>OS ATIVOS MAIS PROCURADOS</h5>

                    <DivWrap>
                        <Link style={{textDecoration: 'none', color: 'white'}} to={'acoes/BBA3'}>
                            <DivCard>
                                <Img src="https://cdn.asupernova.com.br/stocks/icons/BBAS3.svg"></Img>

                                <PCard>BBA3</PCard>

                                <p>BCO BRASIL S.A</p>
                            </DivCard>
                        </Link>

                        <Link style={{textDecoration: 'none', color: 'white'}} to={'acoes/BBDC3'}>
                            <DivCard>
                                <Img src="https://cdn.asupernova.com.br/stocks/icons/BBDC3.svg"></Img>

                                <PCard>BBDC3</PCard>

                                <p>BCO BRADESCO S.A</p>
                            </DivCard>
                        </Link> 
                        
                        <Link style={{textDecoration: 'none', color: 'white'}} to={'acoes/WEGE3'}>
                            <DivCard>
                                <Img src="https://cdn.asupernova.com.br/stocks/icons/WEGE3.svg"></Img>

                                <PCard>WEGE3</PCard>

                                <p>WEG S.A</p>
                            </DivCard>
                        </Link>
    
                        <Link style={{textDecoration: 'none', color: 'white'}} to={'acoes/VALE3'}>
                            <DivCard>
                                <Img src="https://cdn.asupernova.com.br/stocks/icons/VALE3.svg"></Img>

                                <PCard>VALE3</PCard>

                                <p>VALE S.A</p>
                            </DivCard>
                        </Link>
    
                        <Link style={{textDecoration: 'none', color: 'white'}} to={'acoes/PETR3'}>
                            <DivCard>
                                <Img style={{marginTop: '16px'}} src="https://cdn.asupernova.com.br/stocks/icons/PETR3.svg"></Img>

                                <PCard>PETR3</PCard>

                                <p style={{fontSize: '14px'}}>PETROLEO BRASILEIRO S.A PETROBRAS</p>
                            </DivCard>
                        </Link>

                        <Link style={{textDecoration: 'none', color: 'white'}} to={'acoes/BBDC4'}>
                            <DivCard>
                                <Img src="https://cdn.asupernova.com.br/stocks/icons/BBDC4.svg"></Img>

                                <PCard>BBDC4</PCard>

                                <p>BCO BRADESCO S.A</p>
                            </DivCard>
                        </Link>
                    </DivWrap>
                </Actions>
            </Container>
        </>
    )
}

const HeaderStyle = styled.header`
    background-color: #173e5b;
    padding: 110px 16% 70px 16%;
`

const H1 = styled.h1`
    inline-size: 570px;
    overflow-wrap: break-word;
    line-height: 50px;
    font-weight: 700;
    color: white;
    margin: 0; 
    font-size: 50px;
`

const P = styled.p`
    color: white;
    inline-size: 600px;
    overflow-wrap: break-word;
    font-weight: 300;
    margin-top: 10px;
    margin-bottom: 20px;
`

const A = styled.a`
    text-decoration: none;
    color: white;
    padding: 10px 20px 10px 20px;
    background-color: hsl(213 100% 51%);
    border-radius: 5px;
    cursor: pointer;

    &:hover{
        transition: 0.2s ease-in-out;
        color: black;
    }
`

const Container = styled.div`
    background-color: #101318; 
    padding: 70px 16% 80px 16%;
    display: flex;
    flex-direction: column;
    align-items: center;
`

const Input = styled.input`
    color: #ccc;
    width: 800px;
    border-radius: 20px;
    padding: 8px 13px 8px 13px;
    background-color: #101318;
    border: solid 1px hsl(206 61% 65%);

    &:focus{
        outline: #173e5b;
    }
`

const Actions = styled.div`
    background-color: #171b26;
    border-radius: 10px;
    padding: 40px 30px 40px 30px;
    margin-top: 80px;
`

const DivCard = styled.div`
  border-radius: 10px;
  background-color: #32384d;
  margin: 10px 10px 10px 10px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  text-align: center;
  width: 200px;
  height: 220px;
  cursor: pointer;
  padding: 0px 15px 0px 15px;
`

const DivWrap = styled.div`
    flex-wrap: wrap; 
    display: flex;
    justify-content: center;
    gap: 10px;
    margin-top: 30px;
    color: white;
`

const Img = styled.img`
    height: 56px;
    width: 56px;
    border-radius: 5px;
    margin-bottom: 5px;
`

const PCard = styled.p`
    font-weight: 600;
    margin-bottom: 5px;
`

const Absolute = styled.div`
    position: absolute;
    background-color: #171b26;
`

export default Home
