import { useState, useEffect } from 'react'
import styled from 'styled-components'

function ShowCards() {
  const [imgs, setImgs] = useState([])
  const [indicators, setIndicators] = useState([])
  const [title, setTitle] = useState([])

  useEffect(() => {
    const getData = async () => {
      const response = await fetch("http://localhost:3000/data-scrapper")
      const output = await response.json()

      setImgs(output.imgs)
      setIndicators(output.indicators)
      setTitle(output.title)
    }
    getData()
  }, [])

  return (
    <div style={{backgroundColor: '#101318', padding: '80px 18% 80px 18%'}}>
      <DisplayWrap>
        {imgs.map((img, index) => {
            return(
              <DivCard key={index}>
                <DivImg>
                  <Img src={img} alt=''></Img>
                </DivImg>

                <h3 style={{textAlign: 'center', marginTop: ''}}>{title[index]}</h3>

                <div style={{display: 'flex'}}>
                  <div>
                    <p>P/L {indicators[index].pl}</p>
                    <p>DY {indicators[index].dy}</p>
                  </div>

                  <div>
                    <p>P/VP {indicators[index].pvp}</p>
                    <p>ROE {indicators[index].roe}</p>
                  </div>
                </div>
              </DivCard>
            ) 
          })
        }
      </DisplayWrap>
    </div>
  )
}

const DivCard = styled.div`
  border-radius: 10px;
  background-color: #32384d;
  margin: 10px 10px 10px 10px;
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 250px;
  height: 260px;
  cursor: pointer;
`

const DisplayWrap = styled.div`
  flex-wrap: wrap; 
  display: flex;
  justify-content: center;
  background-color: #171b26;
  border-radius: 10px;
  padding: 40px 0px 40px 0px;
  gap: 10px;
`

const DivImg = styled.div`
  background-color: white;
  border-top-left-radius: 10px;
  border-top-right-radius: 10px;
  width: 100%;
  display: flex;
  justify-content: center;
  height: 150px;
`

const Img = styled.img`
  padding: 20px;
  width: 100%;
`

export default ShowCards
