import { React, useEffect, useState }  from "react"
import { useParams } from "react-router-dom"

const ActionDetails = () => {
    const [showImg, setImg] = useState(null)
    const [showName, setName] = useState(null)

    var { action } = useParams()

    useEffect(() => {   
        const postData = async () => {
            await fetch('http://localhost:3002/post-action', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({action: action})
            })
        }
        postData()
        .then
    }, [action])

    useEffect(() => {
        const getData = async () => {
            const response = await fetch('http://localhost:3002/action-details')
            const output = await response.json()

            setName(output.subHeader.name)
            setImg(output.subHeader.src)
        }
        getData()
    }, [])

    return(
        <>
            <img alt='' src={showImg}></img>
            <p>{showName}</p>
        </>
    )
}

export default ActionDetails
