import React, { useEffect, useState } from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";

const Home = () => {
    const [showData, setData] = useState([])

    useEffect(() => {
        const getData = async() => {
            const response = await fetch('http://localhost:3000/data-home')
            const data = await response.json()
            
            setData(data)
        }
        getData()
    }, [])

    return(
        <>
            <HeaderStyle>
                <div>
                    <H1>Decida no que investir em segundos</H1>
                    <P>Nossa ferramenta oferece acesso a dados atualizados, ajudando você a tomar decisões fundamentadas com confiança.</P>
                    <Link style={{textDecoration: 'none'}} to={'acoes/page/1'}>
                        <A>Ver ativos</A>
                    </Link>
                </div>
            </HeaderStyle>

            <Container>
                <Input placeholder="Pesquise pelo ativo desejado" type="text"></Input>

                <Actions>
                    
                    <DivCard>
                        
                    </DivCard>
                </Actions>
            </Container>
        </>
    )
}

const HeaderStyle = styled.header`
    background-color: #173e5b;
    padding: 110px 16% 70px 16%;
`

const H1 = styled.h1`
    inline-size: 570px;
    overflow-wrap: break-word;
    line-height: 50px;
    font-weight: 700;
    color: white;
    margin: 0; 
    font-size: 50px;
`

const P = styled.p`
    color: white;
    inline-size: 600px;
    overflow-wrap: break-word;
    font-weight: 300;
    margin-top: 10px;
    margin-bottom: 20px;
`

const A = styled.a`
    text-decoration: none;
    color: white;
    padding: 10px 20px 10px 20px;
    background-color: hsl(213 100% 51%);
    border-radius: 5px;
    cursor: pointer;

    &:hover{
        transition: 0.2s ease-in-out;
        color: black;
    }
`

const Container = styled.div`
    background-color: #101318; 
    padding: 70px 18% 80px 18%;
    display: flex;
    flex-direction: column;
    align-items: center;
`

const Input = styled.input`
    color: #ccc;
    width: 800px;
    border-radius: 20px;
    padding: 8px 13px 8px 13px;
    background-color: #101318;
    border: solid 1px hsl(206 61% 65%);

    &:focus{
        outline: #173e5b;
    }
`

const Actions = styled.div`
    background-color: #32384d;
    border-radius: 10px;
    padding: 30px;
    margin-top: 50px;
`

const DivCard = styled.div`
  border-radius: 10px;
  background-color: #32384d;
  margin: 10px 10px 10px 10px;
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 250px;
  height: 330px;
  cursor: pointer;
`

export default Home
